KISSY.add('app/views/manage/site/site_guide', function (S, Node, View, MM, Util) {
    var $ = Node.all;
    return View.extend({
        init: function (e) {
            var me = this;
            me.on('save', function (ev) {
                Util.hideDialog();
                me.render();
            });
        },
        render: function () {
            var me = this;
            var loc = me.location;
            me.manage(MM.fetchAll([{ name: 'guide_list' }], function (MesModel) {
                var site = MesModel.get('data');
                var list = site.guideList;
                var limited = site.maxSiteNum <= list.length;
                me.manage('list', list);
                me.setViewPagelet({
                    list: list,
                    limited: limited,
                    maxSiteNum: site.maxSiteNum,
                    pageCount: list.length
                });
            }));
        },
        events: {
            click: {
                add: function (e) {
                    e.halt();
                    var me = e.view;
                    var top = $('#' + e.currentId).parent('.toolbar').offset().top;
                    var dialogConfig = Util.getDefaultDialogConfig({
                        width: 550,
                        top: top
                    });
                    var viewName = 'app/views/manage/site/site_guide_add';
                    var viewOptions = {
                        triggerView: e.view,
                        guideList: me.getManaged('list')
                    };
                    Util.showDialog(dialogConfig, viewName, viewOptions);
                },
                edit: function (e) {
                    e.halt();
                    var me = e.view;
                    var top = $('#' + e.currentId).parent('tr').offset().top;
                    var dialogConfig = Util.getDefaultDialogConfig({
                        width: 550,
                        top: top
                    });
                    var viewName = 'app/views/manage/site/site_guide_add';
                    var viewOptions = {
                        triggerView: e.view,
                        guideID: e.params.guideID
                    };
                    Util.showDialog(dialogConfig, viewName, viewOptions);
                },
                del: function (e) {
                    e.halt();
                    var me = e.view;
                    var top = $('#' + e.currentId).parent('tr').offset().top;
                    var dialogConfig = Util.getDefaultDialogConfig({
                        width: 400,
                        top: top
                    });
                    var viewName = 'app/views/util/confirm';
                    var viewOptions = {
                        confirmFn: function () {
                            me.manage(MM.fetchAll([{
                                    name: 'guide_del',
                                    postParams: { siteId: e.params.guideID }
                                }], function (MesModel) {
                                Util.hideDialog();
                                me.render();
                            }));
                        },
                        cancelFn: function () {
                            Util.hideDialog();
                        },
                        confirmTitle: '\u5220\u9664\u5BFC\u8D2D\u63A8\u5E7F',
                        confirmContent: '\u5220\u9664\u5BFC\u8D2D\u63A8\u5E7F\u524D\u8BF7\u786E\u8BA4\u5DF2\u7ECF\u5220\u9664\u4E86\u8BE5\u5BFC\u8D2D\u63A8\u5E7F\u4E0B\u7684\u6240\u6709\u63A8\u5E7F\u4F4D\uFF0C\u5220\u9664\u5BFC\u8D2D\u63A8\u5E7F\u540E\u8BE5\u5BFC\u8D2D\u63A8\u5E7F\u5BF9\u5E94\u7684\u6240\u6709\u63A8\u5E7F\u4EE3\u7801\u548C\u4EA7\u54C1\u6743\u9650\u7B49\u4FE1\u606F\u90FD\u5C06\u5931\u6548\u3002\u60A8\u786E\u8BA4\u8981\u5220\u9664\u8BE5\u5BFC\u8D2D\u63A8\u5E7F\u5417\uFF1F\u8BF7\u8C28\u614E\u64CD\u4F5C\uFF01'
                    };
                    Util.showDialog(dialogConfig, viewName, viewOptions);
                },
                info: function (e) {
                    e.halt();
                    var me = e.view;
                    var top = $('#' + e.currentId).parent('tr').offset().top;
                    var dialogConfig = Util.getDefaultDialogConfig({ top: top });
                    var viewName = 'app/views/manage/site/site_guide_info';
                    var viewOptions = { guideID: e.params.guideID };
                    Util.showDialog(dialogConfig, viewName, viewOptions);
                }
            }
        },
        renderer: {
            list: {
                status: function (self) {
                    switch (this.siteStatus) {
                    case 'W':
                    case 'T':
                        return '<span class="color-orange">\u63D0\u4EA4\u4E2D</span>';
                        break;
                    case 'P':
                        return '<span class="color-green">\u63D0\u4EA4\u6210\u529F</span>';
                        break;
                    case 'R':
                        return '<span class="color-red">\u62D2\u7EDD</span>';
                        break;
                    case 'V':
                        return '<span class="color-red">\u5386\u53F2\u5907\u6848\u5F85\u4FEE\u6539</span>';
                        break;
                    }
                }
            }
        }
    });
}, {
    requires: [
        'node',
        'mxext/view',
        'app/models/modelmanager',
        'app/util/util',
        'mxext/mmanager',
        'app/models/model',
        'app/models/basemodel',
        'mxext/model',
        'ajax',
        'app/util/datepicker/datepicker',
        'app/util/dialog/dialog',
        'app/util/format/format',
        'app/util/globaltip/globaltip',
        'app/util/robot/sourceid',
        'app/util/spmlog/spmlog',
        'app/util/mathextend/mathextend',
        'app/util/tooltip/tooltip',
        'app/util/widgetds/widgetds',
        'app/util/rank/rank',
        'app/util/reporttip/reporttip',
        'app/util/vcode/vcode',
        'app/util/pagination/index',
        'app/util/fields/fields',
        'app/util/mouseevent/index',
        'magix/vframe',
        'magix/vom',
        'magix/router',
        'brix/gallery/datepicker/index',
        'brix/gallery/calendar/index',
        'brix/gallery/dialog/index',
        'app/util/spmlog/pathmap'
    ]
});